import webbrowser
import urllib.parse
import time
def open_gmail(receiver, subject, body):
    # Create the Gmail compose URL with the encoded receiver, subject, and body
    base_url = "https://mail.google.com/mail/?view=cm&fs=1&tf=1"
    receiver = urllib.parse.quote_plus(receiver)
    subject = urllib.parse.quote_plus(subject)
    body = urllib.parse.quote_plus(body)
    compose_url = f"{base_url}&to={receiver}&su={subject}&body={body}"
    webbrowser.open(compose_url)
